      public function getLatestReadFromAll()
      {
            return $this->createQueryBuilder("a")
             ->select("d.id, d.libelle, c.nom, c.prenom, a.access, d.created")   
             ->innerJoin("a.dossier", "d")
             ->innerJoin("d.client", "c")                
             ->setMaxResults(10)       
             ->orderBy('a.access', 'DESC')                     
             ->getQuery()
             ->getResult();   
      }


